import './App.css';
import { gsap } from 'gsap';
import { useEffect, useRef, useState } from 'react';

const imgStyle = {
  width: "200px",
  height: "200px",
  objectFit: "cover",
  display: "none",
  opacity: "0.0",
}

function App() {
  useEffect(() => {
    const parentBoxes = gsap.utils.toArray(".y");

    parentBoxes.splice(-1);


    let tl = gsap.timeline({
      defaults: {
        duration: 5,
        ease: "power4.out",
        stagger: {
          from: "start",
          each: 0.1
        }
      }
    });

    tl
      .to(parentBoxes, {
        keyframes: [
          { scale: 1.5, rotation: 45 },
          { scale: 1, rotation: 0 }
        ]
      })
  }, [])


  const parent = useRef();

  return (
    <div className="App" ref={parent}>
      <body>
      <div id="wrap">
	<div class="parent-container container">
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="child-container container">
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
			<div class="box"></div>
		</div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
		<div class="box"></div>
	</div>
	</div>
      </body>
    </div>
  );
}



export default App;
