import './App.css';
import { gsap } from 'gsap';
import { useEffect, useRef, useState } from 'react';

const imgStyle = {
  width: "200px",
  height: "200px",
  objectFit: "cover",
  display: "none",
  opacity: "0.0",
}

function App() {
  const tl = useRef();
  const tl2 = useRef();




  useEffect(() => {
    const parentBoxes = gsap.utils.toArray(".parent-container > *");
    const childBoxes = gsap.utils.toArray(".child-container > *");
    const childContainer = document.querySelector(".child-container");

    let index = parentBoxes.indexOf(childContainer);
    let parentBoxesTwo = parentBoxes.splice(index + 1);

    parentBoxes.splice(-1);


    let tl = gsap.timeline({
      defaults: {
        duration: 1.5,
        ease: "power4.out",
        stagger: {
          from: "start",
          each: 0.3
        }
      }
    });

    tl
      .to(parentBoxes, {
        keyframes: [
          { scale: 3.5, rotation: 85},
          { scale: 1, rotation: 0 }
        ]
      })
      .to(childBoxes, {
        duration: 2,
        ease: "back.out",
        stagger: {
          from: "edges",
          each: 0.05
        },
        keyframes: [{ scale: 2 }, { scale: 1 }]
      }, "+=2")
      .to(parentBoxesTwo, {
        keyframes: [
          { scale: 3.5, rotation: 85 },
          { scale: 1, rotation: 0 }
        ]
      }, "-=1.5")

  }, [])

  useEffect(() => {
    setTimeout (() =>{

    tl2.current = gsap.timeline()
        .to ('resto',{displ})
        .to(q('#i0'), { opacity: "1.0", duration: 0.50, stagger: 0.20 })
        .to(q('#i1'), { opacity: "0.0", duration: 0.50, stagger: 0.10 })
      },1.7)
    },[])

  const parent = useRef();

  return (
    <div className="App" ref={parent}>
      <body>
        <div id="wrap">
          <div class="parent-container container">
            <img id ='i0' src={'/img0.jpg'}></img>
            <img id ='i1' src={'/img1.jpg'}></img>
            <img id ='i2' src={'/img2.jpg'}></img>
            <img id ='i3' src={'/img3.jpg'}></img>
            <img id = 'resto' src={'/img0.jpg'}></img>
            <img id = 'resto' src={'/img1.jpg'}></img>
            <img id = 'resto' src={'/img2.jpg'}></img>
            <img id = 'resto' src={'/img3.jpg'}></img>
            <img id = 'resto' src={'/img0.jpg'}></img>
            <img id = 'resto' src={'/img1.jpg'}></img>
            <img id = 'resto' src={'/img2.jpg'}></img>
            <img id = 'resto' src={'/img3.jpg'}></img>
            <img id = 'resto' src={'/img0.jpg'}></img>
            <img id = 'resto' src={'/img1.jpg'}></img>
            <img id = 'resto' src={'/img2.jpg'}></img>
          </div>
        </div>
      </body>
    </div>
  );
}



export default App;
