import './App.css';
import { gsap } from 'gsap';
import { useEffect, useRef, useState } from 'react';

const imgStyle = {
  width: "200px",
  height: "200px",
  objectFit: "cover",
  display: "none",
  opacity: "0.0",
}

function App() {
  const tl = useRef();
  const tl2 = useRef();
  const tl3 = useRef();
  const parent = useRef();
  const q = gsap.utils.selector(parent)
  const [showText, setShowText] = useState(false);
  const [glitch, setGlitch] = useState(false);

  useEffect(() => {
    tl.current = gsap.timeline()
      .to(q('#i'), { display: "block" })
      .to(q('#y'), { display: "block" })
      .to(q('#i'), { opacity: "1.0", duration: 0.60, stagger: 0.35 })
      .to(q('#y'), { opacity: "1.0", duration: 0.30, stagger: 0.60 })
      .to(q('#i'), { opacity: "0.0", duration: 0.60, stagger: 0.35 })
      .to(q('#y'), { opacity: "0.0", duration: 0.30, stagger: 0.60 })
      .to(q('#i'), { display: "none" })
      .to(q('#y'), { display: "none" })
      .repeat(1)
      .delay(1)


  }, [])

  useEffect(() => {
    if (showText) {
      tl2.current = gsap.timeline()
        .to(q('#t'), { opacity: "1.0", duration: 0.50, stagger: 0.20 })
        .to(q('#t'), { opacity: "0.0", duration: 0.50, stagger: 0.10 })
    }
  }, [showText])

  useEffect(() => {
    if (glitch) {
      tl3.current = new gsap.timeline()
        .to(q('body'), { backgroundColor: "white", duration: 0.10 })
        .to(q('body'), { backgroundColor: "black", duration: 0.10 })
        .repeat(1)

    }
  }, [])



useEffect(()=> {
  gsap.defaults({ delay: 1 })

  gsap.to('.one', {
    ease: 'power1.out',
    duration: 5,
    x: 50,
    xPercent: -100
  })

  gsap.to('.two', {
    ease: 'power2.out',
    duration: 5.5,
    x: 100,
    xPercent: -100 
  })

  gsap.to('.therre', {
    ease: 'power3.out',
    duration: 6,
    x: 200,
    xPercent: -200 
  })
})



  return (
    <div className="App" ref={parent}>
      <body>
        <div id='parent' style={{ display: "flex", flexDirection: "row", flexWrap: "wrap" }}>
          <img id='i' src="/img0.jpg" alt="img0" style={{ ...imgStyle, margin: "0px 2000px 0px 0px" }} />
          <img id='y' src="/img1.jpg" alt="img1" style={{ ...imgStyle, margin: "0px 2000px 0px 200px" }} />
          <img id='i' src="/img2.jpg" alt="img2" style={{ ...imgStyle, margin: "0px 2000px 0px 400px" }} />
          <img id='y' src="/img3.jpg" alt="img3" style={{ ...imgStyle, margin: "0px 2000px 0px 600px" }} />
          <img id='i' src="/img4.jpg" alt="img4" style={{ ...imgStyle, margin: "0px 2000px 0px 800px" }} />
          <img id='y' src="/img5.jpg" alt="img5" style={{ ...imgStyle, margin: "0px 2000px 0px 1000px" }} />





          <div style={{ display: "flex", width: "100%", height: "100%", alignItems: "center", justifyContent: "center", }}>
            <p id='t'>I'll </p>
            <p id='t'>remind </p>
            <p id='t'>you </p>
            <p id='t'>how </p>
            <p id='t'>it </p>
            <p id='t'>feels </p>
            <p id='t'>like... </p>
          </div>
        </div>
      </body>
    </div>
  );
}

export default App;
